mapper.select:
SELECT num, category, title, body, writer,
reg_date regDate
FROM board
WHERE writer = #{writer} and body = #{body}
and title = #{title} and category = #{category}

mapper.update:
UPDATE BOARD
SET category
= #{category}, title = #{title}, body = #{body}
WHERE num = #{num}

UPDATE comment
SET body
= #{body}
WHERE cnum = #{cnum}

mapper.select:
SELECT bnum, pnum, max
FROM party
WHERE bnum = #{value}

mapper.update:
UPDATE party
SET max =
#{max}
WHERE bnum = #{bnum}

mapper.select:
SELECT pnum, id
FROM partyMember
WHERE pnum = #{value}
